package week2day2;

public class Factorial { 

	public static void main(String[] args) { 
		// TODO Auto-generated method stub
int fact=1; 
for(int i=7;i>=1;i--) { 
fact=i*fact; 
System.out.println(fact); 
	} 
	} 

} 

